package JavaBasics;

public class PatternPrograms {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1;i<=5;i++)
		{
			
		   for(int j=1;j<=i;j++) {
			   
			   
			System.out.print("*");
		
		}
			
		   System.out.println("");
			
		}
		
		
		
		for(int i=1;i<=5;i++) {
						
		 for(int j=5;j>=i;j--) {
						
						
			System.out.print("*");	
		}
            System.out.println();		
            
        }
		
		
		
		
		
		for(int a=1;a<=5;a++) {
			
		   
			for(int b=1;b<=a;b++) {
			
		     
				System.out.print("*");
		
		 }
		 System.out.println();	
		
		}
		
		        for(int c=1;c<=5;c++) {
			
			
		            for(int d=4;d>=c;d--) {
			
		        System.out.print("*");	
			
		}
		             System.out.println();	
		}
        
		
		
		
		
	
	}

}
